import VueResource from 'vue-resource';
import Vue from 'vue';
import ApiHelper from "./apiHelpers.js";
import qs from 'qs';
Vue.use(VueResource);
export default {
  get: function get(endpoint, params) {
    return new Promise(function (resolve, reject) {
      var url = ApiHelper.handleApiUrl(endpoint, params);
      var header = {
        headers: ApiHelper.baseHeader()
      };
      Vue.http.get(url, header).then(function (response) {
        return resolve(response.body.results);
      }).catch(function (err) {
        console.log(endpoint + ' error' + JSON.stringify(err));
        console.log(err);
        reject(err);
      });
    });
  },
  post: function post(endpoint, data) {
    return new Promise(function (resolve, reject) {
      var url = ApiHelper.handleApiUrl(endpoint);
      var header = {
        headers: ApiHelper.baseHeader()
      };
      Vue.http.post(url, qs.stringify(data), header).then(function (response) {
        return resolve(response.body.results);
      }).catch(function (err) {
        console.log(endpoint + ' error' + JSON.stringify(err));
        console.log(err);
        reject(err);
      });
    });
  },
  postWithFile: function postWithFile(endpoint, data) {
    return new Promise(function (resolve, reject) {
      var url = ApiHelper.handleApiUrl(endpoint);
      var header = {
        headers: ApiHelper.baseHeader()
      };
      header.headers['content-type'] = 'multipart/form-data';
      Vue.http.post(url, data, header).then(function (response) {
        console.log(response);
        resolve(response.body.results);
      }).catch(function (err) {
        console.log(endpoint + ' error' + JSON.stringify(err));
        console.log(err);
        reject(err);
      });
    });
  }
};
