import ProductAPI from '@/api/product'

import { parseTime } from '@/utils'
import Pagination from '@/components/Pagination' // secondary package based on el-pagination
import { mapGetters } from 'vuex'

export default {
  name: 'ComplexTable',
  components: { Pagination },
  filters: {},
  data() {
    return {
      tableKey: 0,
      products: null,
      productTypes: null,
      selectedProductSubTypes: null,
      total: 0,
      listLoading: true,
      dialogFormVisible: false,
      typeRowSpans: null,
      subTypeRowSpans: null,
      listQuery: {
        page: 1,
        limit: 10
      },
      temp: {
        title: null,
        type: null,
        sub_type: null,
        size: null,
        cost: null,
        price: null,
        note: null
      }
    }
  },
  created() {
  },
  computed: {
    ...mapGetters([
      'id'
    ])
  },
  beforeRouteEnter (to, from, next) {
    next(vm => { 
      vm.getProducts()
      vm.getCount()
      vm.getProductTypes()
      next();
    }) 
  },
  methods: {
    calculateRowSpan() {
      let g = []
      let sg = []
      let count = 0
      let scount = 0
      this.products.forEach(i => {
        let found = false
        g.forEach(t => {
          if (t.product_type_id === i.product_type_id) {
            found = true
            t.end++
            t.rowSpan++
            count = t.start + t.rowSpan
          }
        })

        if (!found) {
          let newT = {
            product_type_id: i.product_type_id,
            start: count,
            end: count+1,
            rowSpan: 1
          }
          count++
          g.push(newT)
        }

        let sfound = false
        sg.forEach(t => {
          if (t.product_sub_type_id === i.product_sub_type_id) {
            sfound = true
            t.end++
            t.rowSpan++
            scount = t.start + t.rowSpan
          }
        })

        if (!sfound) {
          let newT = {
            product_sub_type_id: i.product_sub_type_id,
            start: scount,
            end: scount+1,
            rowSpan: 1
          }
          scount++
          sg.push(newT)
        }
      });
      this.typeRowSpans = g;
      this.subTypeRowSpans = sg;
    },
    objectSpanMethod({ row, column, rowIndex, columnIndex }) {
      if (columnIndex === 1) {
        let data = {
          rowspan: 0,
          colspan: 0
        };
        this.typeRowSpans.forEach(i => {
          if (rowIndex >= i.start && rowIndex < i.end && (rowIndex - i.start) % i.rowSpan === 0) {
            data = {
              rowspan: i.rowSpan,
              colspan: 1
            };
            return;
          }
        })

        return data;
      } else if (columnIndex === 2) {
        let data = {
          rowspan: 0,
          colspan: 0
        };
        this.subTypeRowSpans.forEach(i => {
          if (rowIndex >= i.start && rowIndex < i.end && (rowIndex - i.start) % i.rowSpan === 0) {
            data = {
              rowspan: i.rowSpan,
              colspan: 1
            };
            return;
          }
        })

        return data;
      }
    },
    getProducts() {
      this.listLoading = true
      ProductAPI.getAllProducts(this.listQuery)
        .then(response => {
          this.products = response
          this.listLoading = false
          this.calculateRowSpan()
        })
        .catch(err => {
          this.$message({
            message: '读取库存失败，请联系徐神检查',
            type: 'error'
          })
          this.listLoading = false
        })
    },
    getCount() {
      ProductAPI.getAllProductCount()
        .then(response => {
          this.total = response.total
        })
    },
    getProductTypes() {
      ProductAPI.getAllProductType()
        .then(response => {
          this.productTypes = response
        })
    },
    getSubType(productType) {
      if (productType) {
        ProductAPI.getProductSubType({product_type_id: productType})
          .then(response => {
            this.selectedProductSubTypes = response
          })
      }
    },
    handleCreate() {
      this.dialogFormVisible = true
    },
    addNewProduct() {
      this.$confirm('确定添加?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        this.sendNewProductRequest()
      }).catch((err) => {   
        console.log(err)     
      });
    },
    sendNewProductRequest() {
      if (!this.temp.title) {
        this.$message({
          message: '必须填写产品名称',
          type: 'error'
        })
    } else {
      this.listLoading = true
      ProductAPI.addProduct(this.temp)
        .then(response => {
          this.listLoading = false
            this.$alert('添加成功', '成功', {
              confirmButtonText: '确定',
              callback: action => {
                this.page = 1
                this.getProducts()
                this.getCount()
                this.selectedProductSubTypes = null
                this.dialogFormVisible = false;
                this.temp = {
                  title: null,
                  type: null,
                  sub_type: null,
                  size: null,
                  cost: null,
                  price: null,
                  note: null
                }
              }
            });
        })
        .catch(err => {
          console.log(err)     
          this.$message({
            message: '添加失败，请联系徐神检查',
            type: 'error'
          })
          this.listLoading = false
        })
      }
    },
    editEvent() {

    },
    deleteEvent(item) {
      this.$confirm('确定删除>>' + item.title + '<<吗？', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        this.sendDeleteEventRequest(item.id)
      }).catch((err) => {   
        console.log(err)     
      });
    },
    sendDeleteEventRequest(id) {
      this.listLoading = true
      EventAPI.deleteEvent(id)
        .then(response => {
          this.listLoading = false
            this.$alert('删除成功', '成功', {
              confirmButtonText: '确定',
              callback: action => {
                this.page = 1
                this.getEvents()
                this.getCount()
              }
            });
        })
        .catch(err => {
          console.log(err)     
          this.$message({
            message: '删除失败，请联系徐神检查',
            type: 'error'
          })
          this.listLoading = false
        })
    }
  }
}
